# Write your MySQL query statement below
select Prices.product_id, IFNULL(ROUND(sum(Prices.price*UnitsSold.units) / sum(UnitsSold.units),2),0) as average_price from prices left join UnitsSold on(Prices.product_id=UnitsSold.product_id AND UnitsSold.purchase_date BETWEEN Prices.start_date AND Prices.end_date) GROUP BY 1;